mapper.insert:
insert into ezs_area (id, addTime, deleteStatus, 
  areaName, level, sequence, 
  parent_id, common, belongsArea
  )
values (#{id,jdbcType=BIGINT}, #{addTime,jdbcType=TIMESTAMP}, #{deleteStatus,jdbcType=BIT}, 
  #{areaName,jdbcType=VARCHAR}, #{level,jdbcType=INTEGER}, #{sequence,jdbcType=INTEGER}, 
  #{parent_id,jdbcType=BIGINT}, #{common,jdbcType=BIT}, #{belongsArea,jdbcType=VARCHAR}
  )

mapper.select:
   SELECT 
   
   FROM ( SELECT a1.id pid,a1.areaName pname,a3.id aid,a3.areaName aname,ec.id cid FROM ezs_area a1,ezs_area a2,ezs_area a3,ezs_customer ec WHERE a1.level = '0' AND a2.level = '1' AND a3.level = '2' AND a2.parent_id = a1.id AND a3.parent_id = a2.id AND (a1.id = ec.area_id OR a2.id = ec.area_id OR a3.id = ec.area_id )
	) area_custo GROUP BY area_custo.pid ORDER BY COUNT(area_custo.cid) DESC

SELECT
 A.id, A.areaName
FROM ezs_area A WHERE A.areaName=#{areaName}
