mapper.select:
SELECT DATE_FORMAT(A.lastModifyDate,'%Y-%m-%d %H:%i') lastModifyDate, A.id, A.name, A.price, A.cncl_num, A.inventory, A.status status, B.status audit_status, B.priceStatus priceStatus FROM ezs_goods A LEFT JOIN ezs_goods_audit_process B on A.id = B.goods_id WHERE 1=1
and A.status = #{status}
and user_id=#{sellerId} order by lastModifyDate desc LIMIT #{page.startPos},#{page.pageSize}

		select count(*) from 	
		ezs_goods
    	WHERE 
		1=1
		
			and   status = #{status} 
		 
		and user_id = #{sellerId}

		select g.id,g.name,g.price,g.inventory,a.path picturePath,d1.name colorName,ar.areaName areaName,d2.name utilName
		from ezs_goods g,ezs_accessory a,ezs_dict d1,ezs_area ar,ezs_dict d2
		where g.goods_main_photo_id = a.id and g.color_id = d1.id and g.area_id = ar.id and g.util_id = d2.id 
		
   			and g.area_id IN 
			
	        	#{areaId}
			
		
	    
		
   			and g.goodClass_id IN 
			
	        	#{goodsClass}
			
		
		
   			and g.color_id IN 
			
	        	#{colorId}
			
		
		
   			and g.form_id IN 
			
	        	#{formId}
			
		
		
	        and g.source like '%${source}%' 
		
		
	        and g.purpose like '%${purpose}%' 
		
		
	        and g.name like '%${name}%' 
		
		
		
	        and g.price >= #{price1,jdbcType=VARCHAR} 
		
		
	        and g.price <= #{price2,jdbcType=VARCHAR} 
		
	    
		
		
	        and g.density >= #{density1,jdbcType=VARCHAR} 
		
		
	        and g.density <= #{density2,jdbcType=VARCHAR} 
		
	    
		
	        and g.cantilever >= #{cantilever1,jdbcType=VARCHAR}
		
		
	        and g.cantilever <= #{cantilever2,jdbcType=VARCHAR}
		
	    
		
	        and g.freely >= #{freely1,jdbcType=VARCHAR}
		
		
	        and g.freely <= #{freely2,jdbcType=VARCHAR}
		
	    
		
	        and g.lipolysis >= #{lipolysis1,jdbcType=VARCHAR}
		
		
	        and g.lipolysis <= #{lipolysis2,jdbcType=VARCHAR}
		
	    
		
	        and g.ash >= #{ash1,jdbcType=VARCHAR}
		
		
	        and g.ash <= #{ash2,jdbcType=VARCHAR}
		
	    
		
	        and g.water >= #{water1,jdbcType=VARCHAR}
		
		
	        and g.water <= #{water2,jdbcType=VARCHAR}
		
	    
		
	        and g.tensile >= #{tensile1,jdbcType=VARCHAR}
		
		
	        and g.tensile <= #{tensile2,jdbcType=VARCHAR}
		
	    
		
	        and g.crack >= #{crack1,jdbcType=VARCHAR}
		
		
	        and g.crack <= #{crack2,jdbcType=VARCHAR}
		
	    
		
	        and g.bending >= #{bending1,jdbcType=VARCHAR}
		
		
	        and g.bending <= #{bending2,jdbcType=VARCHAR}
		
	    
		
	        and g.flexural >= #{flexural1,jdbcType=VARCHAR}
		
		
	        and g.flexural <= #{flexural2,jdbcType=VARCHAR}
		
	    
		
		
	        ORDER BY g.addTime desc
		
		
		
	        ORDER BY g.inventory desc
		
		
	        ORDER BY g.inventory asc
		
	    limit #{pageStart},#{pageSize}
