mapper.insert:
insert into ezs_goodscart (id, addTime, deleteStatus, 
  cart_type, count, price, 
  goods_id, of_id, sc_id
  )
values (#{id,jdbcType=BIGINT}, #{addTime,jdbcType=TIMESTAMP}, #{deleteStatus,jdbcType=BIT}, 
  #{cart_type,jdbcType=VARCHAR}, #{count,jdbcType=DOUBLE}, #{price,jdbcType=DECIMAL}, 
  #{goods_id,jdbcType=BIGINT}, #{of_id,jdbcType=BIGINT}, #{sc_id,jdbcType=BIGINT}
  )

mapper.select:
SELECT g.id,g.cart_type,g.count,g.price,g.goods_id, (select CONCAT(d1.`name`,d2.`name`,a.`name`) name from ezs_goods a,ezs_dict d1,ezs_dict d2 where a.color_id = d1.id and a.form_id = d2.id and a.id = g.goods_id) gname, gs.inventory, gs.area_id gAreaId, CONCAT("http://m.ezaisheng.com/",REPLACE(ga.path,'\\','/'),"/",ga.`name`) picturePath, d.name utilName, a2.areaName FROM ezs_goodscart g LEFT JOIN ezs_goods gs on g.goods_id = gs.id LEFT JOIN ezs_accessory ga on gs.goods_main_photo_id = ga.id LEFT JOIN ezs_dict d on gs.util_id = d.id LEFT JOIN ezs_area a2 on gs.area_id = a2.id WHERE g.id
#{item}
AND g.deleteStatus = '0' and g.of_id IS NULL
