mapper.select:
select count(*) acount,sum(price) aprice,order_type from ezs_payinfo 
where  deleteStatus = #{deleteStatus}
	
		and addTime >=#{starttime,jdbcType=VARCHAR}
	
	
		 and addTime <=#{endtime,jdbcType=VARCHAR}
	
	
		 and order_type = #{order_type,jdbcType=INTEGER}
	
	
		 and pay_mode = #{pay_mode,jdbcType=INTEGER}
	
	group by order_type

select a.addTime,a.pay_mode,a.order_type,a.price,c.name from ezs_payinfo a 
left join ezs_dvaluate b on a.order_no = b.id 
left join ezs_goods c on b.goods_id = c.id 
where a.deleteStatus = #{deleteStatus}
	
		and a.addTime >=#{starttime,jdbcType=VARCHAR}
	
	
		and a.addTime <=#{endtime,jdbcType=VARCHAR}
	
	
		and a.order_type = #{order_type,jdbcType=INTEGER}
	
	
		and a.pay_mode = #{pay_mode,jdbcType=INTEGER}
	 	
	order by a.addTime desc
